import { Component, OnInit } from '@angular/core';
import { AbstractControl, FormControl, FormGroup, ValidationErrors, ValidatorFn, Validators } from '@angular/forms';
import { Router } from '@angular/router';

export function passwordMatchValidator(passwordKey: string, confirmPasswordKey: string): ValidatorFn {
  return (group: AbstractControl): ValidationErrors | null => {
    const password = group.get(passwordKey)?.value;
    const confirmPassword = group.get(confirmPasswordKey)?.value;

    if (password !== confirmPassword) {
      return { passwordsMismatch: true };
    }
    return null;
  };
}

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.scss']
})
export class SignUpComponent implements OnInit {
  public signUpForm!: FormGroup;

  constructor(public router: Router) {}

  ngOnInit(): void {
    this.signUpForm = new FormGroup({
      username: new FormControl('', [Validators.required, Validators.minLength(4)]),
      email: new FormControl('', [Validators.required, Validators.email]),
      password: new FormControl('', [Validators.required]),
      passwordAgain: new FormControl('', [Validators.required])
    },{
      validators: passwordMatchValidator('password', 'passwordAgain')
    });

    console.log(this.signUpForm.get('email')?.invalid && this.signUpForm.get('email')?.dirty);
  }

  public onClickLogin() {
    this.router.navigateByUrl("/login");
  }
}
<div class="sign-up-container">
    <div class="sign-up-form-container">
        <h1>Sign up</h1>
        <div class="error-container">
            <div>Már foglalt felhasználónév</div>
            <div>Már foglalt email</div>
        </div>
        <div class="sign-up-box">
            <form>
                <div class="form-item">
                    <label>Username:</label>
                    <input/>
                    <div *ngIf="this.signUpForm.get('email')?.invalid && this.signUpForm.get('email')?.dirty" class="error-box">
                        Invalid
                    </div>
                </div>
                <div class="form-item">
                    <label>Email:</label>
                    <input/>
                    <div *ngIf="this.signUpForm.get('email')?.invalid && this.signUpForm.get('email')?.dirty" class="error-box">
                        Invalid
                    </div>
                </div>
                <div class="form-item">
                    <label>Password:</label>
                    <input/>
                    <div class="error-box">

                    </div>
                </div>
                <div class="form-item">
                    <label>Password again:</label>
                    <input/>
                    <div class="error-box">

                    </div>
                </div>
                <button>Sign up</button>
            </form>
        </div>
        <p>Go to <span class="link" (click)="onClickLogin()">login</span>!</p>
    </div>
</div>
