import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  public loginForm = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', [Validators.required])
  });

  constructor(public router: Router) {}

  public onClickSignUp() {
    this.router.navigateByUrl("/sign-up");
  }
}
<div class="login-container">
    <div class="login-form-container">
        <h1>Login</h1>
        <div class="login-box">
            <form [formGroup]="this.loginForm">
                <div class="form-item">
                    <label>Email:</label>
                    <input formControlName="email" type="email"/>
                    <div *ngIf="this.loginForm.get('email')?.invalid && this.loginForm.get('email')?.touched && !this.loginForm.get('email')?.errors!['required']" class="error-box">
                        Invalid
                    </div>
                    <div *ngIf="this.loginForm.get('email')?.invalid && this.loginForm.get('email')?.touched && this.loginForm.get('email')?.errors!['required']" class="error-box">
                        Kötelező megadni
                    </div>
                </div>
                <div class="form-item">
                    <label>Password:</label>
                    <input formControlName="password" type="password"/>
                   <div *ngIf="this.loginForm.get('password')?.invalid && this.loginForm.get('password')?.touched && this.loginForm.get('password')?.errors!['required']" class="error-box">
                        Kötelező megadni
                    </div>
                </div>
                <button [disabled]="this.loginForm.invalid">Login</button>
            </form>
        </div>
        <p>Go to <span class="link" (click)="onClickSignUp()">sign up</span>!</p>
    </div>
</div>
