import { Component, OnInit } from '@angular/core';
import { AbstractControl, FormControl, FormGroup, ValidationErrors, ValidatorFn, Validators } from '@angular/forms';
import { Router } from '@angular/router';

export function passwordMatchValidator(passwordKey: string, confirmPasswordKey: string): ValidatorFn {
  return (group: AbstractControl): ValidationErrors | null => {
    const password = group.get(passwordKey)?.value;
    const confirmPassword = group.get(confirmPasswordKey)?.value;

    if (password !== confirmPassword) {
      console.log("nem egyezik meg");
      return { passwordsMismatch: true };
    }
    return null;
  };
}

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.scss']
})
export class SignUpComponent implements OnInit {
  public signUpForm!: FormGroup;
  public reservedEmailOrUsername: boolean = false;

  constructor(public router: Router) {}

  ngOnInit(): void {
    this.signUpForm = new FormGroup({
      username: new FormControl('', [Validators.required, Validators.minLength(4)]),
      email: new FormControl('', [Validators.required, Validators.email]),
      password: new FormControl('', [Validators.required]),
      passwordAgain: new FormControl('', [Validators.required])
    },{
      validators: passwordMatchValidator('password', 'passwordAgain')
    });
  }

  public onClickLogin() {
    console.log(this.signUpForm);
    //this.router.navigateByUrl("/login");
  }
}
<div class="sign-up-container">
    <div class="sign-up-form-container">
        <h1>Sign up</h1>
        <div *ngIf="reservedEmailOrUsername" class="error-container">
            <div>Már foglalt email/felhasználónév</div>
        </div>
        <div class="sign-up-box">
            <form [formGroup]="this.signUpForm">
                <div class="form-item">
                    <label>Username:</label>
                    <input formControlName="username" type="text"/>
                    <div *ngIf="this.signUpForm.get('username')?.invalid && this.signUpForm.get('username')?.touched" class="error-box">
                        Invalid
                    </div>
                </div>
                <div class="form-item">
                    <label>Email:</label>
                    <input formControlName="email" type="email"/>
                    <div *ngIf="this.signUpForm.get('email')?.invalid && this.signUpForm.get('email')?.touched" class="error-box">
                        Invalid
                    </div>
                    <div *ngIf="this.signUpForm.get('email')?.invalid && this.signUpForm.get('email')?.touched && this.signUpForm.get('email')?.errors!['required']" class="error-box">
                        Invalid
                    </div>
                </div>
                <div class="form-item">
                    <label>Password:</label>
                    <input formControlName="password" type="password"/>
                    <div *ngIf="this.signUpForm.get('password')?.invalid && this.signUpForm.get('password')?.touched" class="error-box">
                        Nem jó jelszó
                    </div>
                </div>
                <div class="form-item">
                    <label>Password again:</label>
                    <input formControlName="passwordAgain" type="password"/>
                    <div *ngIf="this.signUpForm.get('passwordAgain')?.invalid && this.signUpForm.get('passwordAgain')?.touched && this.signUpForm?.errors!['passwordsMismatch']" class="error-box">
                        Nem egyeznek meg a jelszavak
                    </div>
                </div>
                <button [disabled]="this.signUpForm.invalid">Sign up</button>
            </form>
        </div>
        <p>Go to <span class="link" (click)="onClickLogin()">login</span>!</p>
    </div>
</div>
